package com.dsaprogram.ml;
import java.util.HashMap;
import java.util.Map;
import java.util.*;
import java.lang.Thread;

public class JVMOutOfMemoryErrorSimulator {
	
	private final static int NB_ITERATION = 5000000;
	
	
	private final static String LEAKING_DATA_PREFIX = "";

	private static Map<String,String> LeakingMap;
	
	static {
		LeakingMap=new HashMap<String,String>();
	}
	
	public static void main(String[] args){
		
		
		System.out.println("JVM outofmemory simulator");
		System.out.println("Author : Ashis Dash");
		
		try{
			for (int i=0;i<NB_ITERATION;i++){
				String data= LEAKING_DATA_PREFIX+i;
				LeakingMap.put(data, data);
//				IdleThread newJavaThread=new IdleThread();
//				newJavaThread.setDaemon(true);
//				newJavaThread.start();
				System.out.println("number of iteration: "+i);
				Thread.sleep(1);
			}
		}catch (Throwable any){
			if(any instanceof java.lang.OutOfMemoryError){
				System.out.println("OutOfMemoryError is triggered: !"+any.getMessage()+"["+any+"]");
				
				}else{
					System.out.println("Unexpected excxception: !"+any.getMessage()+"["+any+"]");	
			}
		}
		System.out.println("Simulator done: ");
	}
	
}
